import { ContentGenerator } from "./content-generator";
import { Fruits } from "./food-fruits";
import { Vegetables } from "./food-vegetables";
import { Alcohol } from "./drinks-alcohol";
import { Nonalcohol } from "./drinks-nonalcohol";
var listWrapper = document.getElementById("product-list");
var products = [];
var apple = new Fruits("apple", "red", 15);
products.push(apple.getInfo());
var banana = new Fruits("banana", "yellow", 30);
products.push(banana.getInfo());
var orange = new Fruits("orange", "orange", 45);
products.push(orange.getInfo());
var tomato = new Vegetables("tomato", "rose", 35);
products.push(tomato.getInfo());
var cucumber = new Vegetables("cucumber", "green", 25);
products.push(cucumber.getInfo());
var onions = new Vegetables("onions", "white", 17);
products.push(onions.getInfo());
var beer = new Alcohol("beer", 25);
products.push(beer.getInfo());
var cognac = new Alcohol('cognac', 100);
products.push(cognac.getInfo());
var liquor = new Alcohol('liquor', 120);
products.push(liquor.getInfo());
var juice = new Nonalcohol("juice", 22);
products.push(juice.getInfo());
var water = new Nonalcohol("water", 10);
products.push(water.getInfo());
var soda = new Nonalcohol("soda", 20);
products.push(soda.getInfo());
ContentGenerator.generateHTML(listWrapper, products);
